sp_widget.client_script:
function($scope) {
	/* widget controller */
	var c = this;

	c.submitIncident = function () {
		$scope.c.data.formSubmitted = true;
		c.server.update();
	}

	c.newIncident = function () {
		c.data.newIssue = true;
	}
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	var debug = false;

	checkTacFeedback();

	if (input){
		if (this.debug) gs.addInfoMessage("Submitting");	
		submitIncident();
	}

	function checkTacFeedback(){
		if (gs.tableExists('x_snc_train_feedbk_feedback')){
			data.newIssue = false;
		}else{
			data.newIssue = true;
		}
	}

	function submitIncident(){
		if (this.debug == true) gs.addInfoMessage("Submitting");

		var instanceID = gs.getProperty('instance_id');
		var request = new sn_ws.RESTMessageV2();
		var cl = gs.getProperty('cl.management.host');

		// Temporary solution to allow for a single place for INCs
		var shortDescription = input.shortDescription;
		if (cl.includes('clabs')) {
			cl = 'cloudlabs';
			shortDescription = '[Clabs 2.0]' + input.shortDescription + '\n\n';
			shortDescription += 'Collection ID: ' + gs.getProperty('cl.collection_id') + '\n';
			shortDescription += 'Instance Name: ' + gs.getProperty('instance_name');
			instanceID = '';
		}

		request.setEndpoint('https://' + cl + '.service-now.com/api/snc/cloud_labs_create_incident');
		request.setHttpMethod('POST');

		request.setRequestHeader("Accept","application/json");
		request.setQueryParameter('instanceID', instanceID);
		request.setQueryParameter('category', input.category);
		request.setQueryParameter('subCategory', '');
		request.setQueryParameter('shortDescription', shortDescription);

		var response = request.execute();
		data.submitted = "true";
		return true;
	}

})();
